import {React, useContext, useState} from "react";
import { GlobalStoreContext } from '../../store/index.js';
import GeneralSettings from './general_settings.js'

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const AppSettings = () => {
    const { store } = useContext(GlobalStoreContext);

    const [menu, setMenu] = useState("general");

    // Get current app's settings
    const changeAdvanced = function(){
        setMenu("advanced");
    }
    const changeGeneral = function(){
        setMenu("general");
    }

    const saveSettings = function(){
        store.save_settings({default_shape: defaultShape});
    }

    const changeDefaultSquare = function(){
        setDefaultShape("square");
    }

    const changeDefaultLong = function(){
        setDefaultShape("long");
    }

    let fields;
    if(menu === "general"){
        if(store.current_app !== null){
            fields = <GeneralSettings />;
        }
        else{
            fields = 
            <div id="system-settings">
                <h1>System Settings</h1>
                <div id="setting-default-box-shape" className="setting-row">
                    <Row className="align-items-center">
                        <Col md="4">
                            <label className="main-label">Default Box Shape</label>
                        </Col>
                        <Col md="8">
                            <Row>
                                <Col md="4">
                                    <input 
                                        type="radio" 
                                        value="square" 
                                        name="default-shape-input" 
                                        id="default-shape-square" 
                                        onChange={changeDefaultSquare}
                                    />
                                    <label 
                                        htmlFor="default-shape-square" 
                                        className={defaultShape === "square" ? "shape-selected" : ""}
                                    >
                                        <div className="setting-square-box"></div>
                                        Small Box
                                    </label>
                                </Col>
                                <Col md="8">
                                    <input 
                                        type="radio" 
                                        value="long" 
                                        name="default-shape-input" 
                                        id="default-shape-long"
                                        onChange={changeDefaultLong}
                                    />
                                    <label 
                                        htmlFor="default-shape-long" 
                                        className={defaultShape === "long" ? "shape-selected" : ""}
                                    >
                                        <div className="setting-long-box"></div>
                                        Long Box
                                    </label>
                                </Col>
                            </Row>
                        </Col>
                    </Row>
                </div>
            </div>
        }
    }
    else if(menu === "advanced"){
        fields = <div>
                    <h1>Advanced Settings</h1>
                </div>;
    }
    else{
        fields = <div>
                    <h1>Unknown Menu</h1>
                    <p>menu</p>
                </div>;
    }

    return(
        <div className="full-width active">
            <Container id="app-settings">
                <Row id="app-setting-body">
                    <Col md={3} id="app-setting-nav">
                        <div onClick={ changeGeneral } >
                            <h2 className={ menu === "general" ? "highlight" : "" }>
                                General
                            </h2>
                        </div>
                        <div onClick={ changeAdvanced } >
                            <h2 className={ menu === "advanced" ? "highlight" : "" }>
                                Advanced
                            </h2>
                        </div>
                    </Col>
                    <Col md={9} id="app-setting-fields">
                        {fields}
                    </Col>
                </Row>
                <Row id="app-setting-footer">
                    <Col>
                        <input type="button" value="Close" onClick={saveSettings}/>
                    </Col>
                </Row>
            </Container>
        </div>
    );
}

export default AppSettings;
