import {React, useContext, useState} from "react";
import { GlobalStoreContext } from '../store/index.js';


const AppBox = ({id, initX, initY}) => {

    const { store } = useContext(GlobalStoreContext);

    let is_active = false;
    if(store.current_app != null && store.current_app.id == id) is_active = true;
    const [dragging, setDragging] = useState(is_active);

    const [x, setX] = useState(initX);
    const [y, setY] = useState(initY);

    let styling = {top: (y - 20) + "px", left: (x - 20) + "px"};

    const handleDown = (e) => {
        console.log(e.clientX);
        console.log(e.clientY);
        let app = {
            id: id,
            initX: e.clientX,
            initY: e.clientY
        }
        store.hold_app(app);
        setDragging(true);
        styling = {top: e.clientY + "px", left: e.clentX + "px"};
    }

    const handleMove = (e) => {
        if(dragging){
            setX(e.clientX);
            setY(e.clientY);
        }
    }

    const handleDrop = () => {
        store.add_app();
        setDragging(false);
    }

    if(dragging){
        styling.transition = "0s";
    }
    else{
        styling.transition = "1s";
    }
    let box_class = "app-box";
    if(store.current_app != null && store.current_app.id == id) box_class += " initial";
    let box_text = id % 2 == 0 ? "Megan" : "Karen";

    return(
        <div className={box_class} style={styling} onMouseDown={handleDown} onMouseMove={handleMove} onMouseUp={handleDrop}>
            <p>{box_text}</p>
        </div>
    )
}

export default AppBox;
