import {React, useContext} from "react";
import Footer from './footer.js';
import Menu from './menu/menu.js';

import { GlobalStoreContext } from '../store/index.js';
import AppBox from './app_box.js';

const MainComponent = () => {

    const { store } = useContext(GlobalStoreContext);

    let open_apps = "";
    open_apps = store.active_apps.map((app) => (
        <AppBox 
            id={app.id}
            initX={app.x}
            initY={app.y}
        />
    ));
    if(store.active_apps.length > 0){
        for(let i=0; i < store.active_apps.length; i++){
            let curr_app = store.active_apps[i];

            open_apps = 
                <AppBox 
                    id={curr_app.id}
                    initX={curr_app.x}
                    initY={curr_app.y}
                />
            ;
        }
    }

    return (
        <div id="container">
            {open_apps}
            <Menu />
            <Footer />
        </div>
    );
}

export default MainComponent;
