import React from "react";
import Footer from './footer.js';
import Menu from './menu.js';
import $ from 'jquery';

const open_menu = () => {
    $("#menu").addClass("active");
    $("#close").show();
    $("#footer").hide();
};

const close_menu = () => {
    $("#menu").removeClass("active");
    $("#close").hide();
    $("#footer").show();
}


const main_component = () => {
    return (
        <div id="container">
            <Menu handleClose={close_menu}/>
            <Footer handleClick={open_menu}/>
        </div>
    );
}

export default main_component;
