import {React, useContext, useState} from "react";
import { GlobalStoreContext } from '../../store/index.js';

import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const GeneralSettings = () => {
    const { store } = useContext(GlobalStoreContext);
    const [boxShape, setBoxShape] = useState(store.current_app.settings.shape);

    const changeShapeSquare = function(){
        setBoxShape("square");
    }

    const changeShapeLong = function(){
        setBoxShape("long");
    }

    return(
        <div id="general-settings">
            <h1>General Settings</h1>
            <div id="setting-box-shape" className="setting-row">
                <Row className="align-items-center">
                    <Col md="4">
                        <label className="main-label">Box Shape</label>
                    </Col>
                    <Col md="8">
                        <Row>
                            <Col md="4">
                                <input 
                                    type="radio" 
                                    value="square" 
                                    name="shape-input" 
                                    id="shape-square" 
                                    onChange={changeShapeSquare}
                                />
                                <label 
                                    htmlFor="shape-square" 
                                    className={boxShape === "square" ? "shape-selected" : ""}
                                >
                                    <div className="setting-square-box"></div>
                                    Small Box
                                </label>
                            </Col>
                            <Col md="8">
                                <input 
                                    type="radio" 
                                    value="long" 
                                    name="shape-input" 
                                    id="shape-long"
                                    onChange={changeShapeLong}
                                />
                                <label 
                                    htmlFor="shape-long" 
                                    className={boxShape === "long" ? "shape-selected" : ""}
                                >
                                    <div className="setting-long-box"></div>
                                    Long Box
                                </label>
                            </Col>
                        </Row>
                    </Col>
                </Row>
            </div>
        </div>
    );

}

export default GeneralSettings;
