import {React, useContext} from "react";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';

import Menu_Item from './menu_item.js';
import { GlobalStoreContext } from '../store/index.js'

const Menu = () => {

    const close_menu = () => {
        $("#menu").removeClass("active");
        $("#close").hide();
        $("#footer").show();
    }

    return(
        <div id="menu">
            <div id="close"><FontAwesomeIcon icon={ faTimes } size="3x" onClick={close_menu} /></div>
            <Menu_Item 
                appType="weather"  
                count="0" 
            />
            <Menu_Item 
                appType="music"  
                count="1" 
            />
            <Menu_Item 
                appType="screen"  
                count="2" 
            />
            <Menu_Item 
                appType="setting"  
                count="3" 
            />
            <Menu_Item 
                appType="calendar"  
                count="4" 
            />
        </div>
    )
};

export default Menu;
