import {React, useContext, useState} from "react";
import { GlobalStoreContext } from '../store/index.js';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

const AppSettings = () => {
    const { store } = useContext(GlobalStoreContext);

    const [menu, setMenu] = useState("general");
    const [boxShape, setBoxShape] = useState("square");

    // Get current app's settings
    const changeAdvanced = function(){
        setMenu("advanced");
    }
    const changeGeneral = function(){
        setMenu("general");
    }

    const closeSettings = function(){

        store.save_app(store.current_app);
    }

    const handleShapeChange = function(){
        alert(boxShape);
        if(boxShape === "square"){
            setBoxShape("advanced");
        }
        else{
            setBoxShape("square");
        }
    }

    let fields;
    if(menu === "general"){
        fields = <Row>
                    <h1>General Settings</h1>
                    <Col md="4">
                        <label>Box Shape</label>
                    </Col>
                    <Col md="8">
                        <Row>
                            <Col md="4">
                                <input 
                                    type="radio" 
                                    value="square" 
                                    name="shape-input" 
                                    id="square-shape" 
                                    onChange={handleShapeChange}
                                />
                                <label 
                                    for="square-shape" 
                                    className={boxShape === "square" ? "shape-selected" : ""}
                                >
                                    <div id="square-box-setting"></div>
                                    Small Box
                                </label>
                            </Col>
                            <Col md="8">
                                <input 
                                    type="radio" 
                                    value="long" 
                                    name="shape-input" 
                                    id="long-shape"
                                />
                                <label 
                                    for="long-shape" 
                                    className={boxShape === "long" ? "shape-selected" : ""}
                                >
                                    <div id="long-box-setting"></div>
                                    Long Box
                                </label>
                            </Col>
                        </Row>
                    </Col>
                </Row>;
    }
    else if(menu === "advanced"){
        fields = <div>
                    <h1>Advanced Settings</h1>
                </div>;
    }
    else{
        fields = <div>
                    <h1>Unknown Menu</h1>
                    <p>menu</p>
                </div>;
    }

    return(
        <div className="full-width active">
            <Container id="app-settings">
                <Row id="app-setting-body">
                    <Col md={3} id="app-setting-nav">
                        <div onClick={ changeGeneral } >
                            <h2 className={ menu === "general" ? "highlight" : "" }>
                                General
                            </h2>
                        </div>
                        <div onClick={ changeAdvanced } >
                            <h2 className={ menu === "advanced" ? "highlight" : "" }>
                                Advanced
                            </h2>
                        </div>
                    </Col>
                    <Col md={9} id="app-setting-fields">
                        {fields}
                    </Col>
                </Row>
                <Row id="app-setting-footer">
                    <Col>
                        <input type="button" value="Close" onClick={closeSettings}/>
                    </Col>
                </Row>
            </Container>
        </div>
    );
}

export default AppSettings;
