import {React, useContext} from "react";
import { GlobalStoreContext } from '../../store/index.js';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCloud } from '@fortawesome/free-solid-svg-icons';
import { faMusic } from '@fortawesome/free-solid-svg-icons';
import { faCalendar } from '@fortawesome/free-solid-svg-icons';
import { faGear } from '@fortawesome/free-solid-svg-icons';
import { faDesktop } from '@fortawesome/free-solid-svg-icons';

const MenuItem = ( { appType, count } ) => {

    const { store } = useContext(GlobalStoreContext);

    const createApp = function(e){
        console.log(e.target);
        let new_app = {
            id: count,
            x: (e.clientX + 20),
            y: (e.clientY + 20)
        };
        store.init_app(new_app);
    }


    let appIcon;
    switch(appType){
        case "weather":
            appIcon = faCloud;
            break;
        case "music":
            appIcon = faMusic;
            break;
        case "calendar":
            appIcon = faCalendar;
            break;
        case "setting":
            appIcon = faGear;
            break;
        case "screen":
            appIcon = faDesktop;
            break;
        default:
            appIcon = "";
    }

    const styling = {"--i": count};
    let classList = "";
    for(let i = 0; i < store.active_apps.length; i++){
        if(store.active_apps[i].id == count){
            classList = " active";
        }
    }

    return(
        <li style={styling} onMouseDown={createApp} className={classList}>
            <div><FontAwesomeIcon icon={ appIcon } size="2x"/></div>
        </li>
    )
}

export default MenuItem;
