import React from "react";
import $ from 'jquery';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTimes } from '@fortawesome/free-solid-svg-icons';

import Menu_Item from './menu/menu_item.js';

const Menu = () => {

    const close_menu = () => {
        $("#menu").removeClass("active");
        $("#close").hide();
        $("#footer").show();
    }

    return(
        <div id="menu">
            <div id="close"><FontAwesomeIcon icon={ faTimes } size="3x" onClick={close_menu} /></div>
            <li style={{"--i" : 0}}>
                <div><Menu_Item appType='weather'/></div>
            </li>
            <li style={{"--i" : 1}}>
                <div><Menu_Item appType='music'/></div>
            </li>
            <li style={{"--i" : 2}}>
                <div><Menu_Item appType='screen' /></div>
            </li>
            <li style={{"--i" : 3}}>
                <div><Menu_Item appType='setting' /></div>
            </li>
            <li style={{"--i" : 4}}>
                <div><Menu_Item appType='calendar' /></div>
            </li>
        </div>
    )
};

export default Menu;
