import {React, useContext, useState} from "react";
import { GlobalStoreContext } from '../store/index.js';


const AppBox = ({id, initX, initY}) => {

    const { store } = useContext(GlobalStoreContext);

    let is_active = false;
    if(store.current_app != null && store.current_app.id == id) is_active = true;
    const [dragging, setDragging] = useState(is_active);

    const [x, setX] = useState(initX);
    const [y, setY] = useState(initY);

    const handleMove = (e) => {
        if(dragging){
            setX(e.clientX);
            setY(e.clientY);
        }
    }

    const handleDrop = () => {
        store.add_item();
        setDragging = false;
    }

    let styling = {top: (y / 2) + "px", left: (x / 2) + "px"};

    return(
        <div className="app-box" style={styling} onMouseMove={handleMove} onMouseUp={handleDrop}>
            <p>Test Box</p>
        </div>
    )
}

export default AppBox;
