import {React, useState} from "react";
//import { GlobalStoreContext } from '../store/index.js';

const AppSettings = () => {
    //const { store } = useContext(GlobalStoreContext);

    const [menu, setMenu] = useState("general");

    // Get current app's settings
    const menuChange = (section) => {
        if(menu !== section){
            //setMenu(section);
        }
    }

    let fields;
    if(menu === "general"){
        fields = <div>
                    <h1>General Settings</h1>
                    <div className="col-4">
                        <label>Box Shape</label>
                    </div>
                    <div className="col-8">
                        <input type="radio" value="square" />
                        <input type="radio" value="long" />
                    </div>
                </div>;
    }
    else if(menu === "advanced"){
        fields = <div>
                    <h1>Advanced Settings</h1>
                </div>;
    }
    else{
        fields = <div>
                    <h1>Unknown Menu</h1>
                    <p>menu</p>
                </div>;
    }

    return(
        <div id="app-settings">
            <div className="row">
                <div className="col-1" id="app-setting-nav">
                    <h2 onClick={ menuChange("general") } 
                        className={ menu === "general" ? "highlight" : "" }
                    >General</h2>
                    <h2 className={ menu === "advanced" ? "highlight" : "" }
                        onClick={ menuChange("advanced") }
                    >Advanced</h2>
                </div>
                <div className="col-11" id="app-setting-fields">
                    {fields}
                </div>
            </div>
        </div>
    );
}

export default AppSettings;
